// Example of creating a simple web test in MSTest\n[TestMethod]\npublic void OpenWebsiteWithWebTest()\n{\n    WebTest webTest = new WebTest();\n    webTest.Request = new Request("http://example.com");\n    \n    var playbackSettings = new WebTestPlaybackPlugin2Configuration();\n    playbackSettings.ExpectedHttpStatusCode = "200";\n    webTest.PlaybackSettings.Add(playbackSettings);\n    \n    webTest.ValidateResponseUrl = true;\n    webTest.RunAsWebTest(null, null, null);\n}