using System;\nusing System.IO;\nusing System.Linq;\n\nclass Program\n{\n    static void Main()\n    {\n        string jobDir = @"C:\Your\Directory\Path";\n        string[] excludeExtensions = { ".txt", ".log" };  // Add extensions to exclude here\n\n        var files = Directory.GetFiles(jobDir)\n                           .Where(file => !excludeExtensions.Contains(Path.GetExtension(file).ToLower()))\n                           .ToArray();\n\n        foreach (var file in files)\n        {\n            Console.WriteLine(file);\n        }\n    }\n}