using System;\nusing System.Collections.Generic;\n\npublic class Ad\n{\n    public int Id { get; set; }\n    public string Content { get; set; }\n    public decimal MaxBudget { get; set; }\n    public decimal DailyBudget { get; set; }\n    public decimal SpentToday { get; set; }\n}\n\npublic class AdManager\n{\n    private List<Ad> _ads;\n    private Random _random;\n\n    public AdManager()\n    {\n        _ads = new List<Ad>();\n        _random = new Random();\n    }\n\n    // Method to add ads from advertisers\n    public void AddAd(Ad ad)\n    {\n        _ads.Add(ad);\n    }\n\n    // Method to fetch an ad based on budget constraints\n    public Ad GetAdToShow()\n    {\n        var eligibleAds = _ads.FindAll(a => a.DailyBudget - a.SpentToday > 0);\n\n        if (eligibleAds.Count == 0) return null;\n\n        // Random selection for simplicity, can be enhanced to consider more factors like ROI or click-through rate\n        int selectedAdIndex = _random.Next(eligibleAds.Count);\n        var selectedAd = eligibleAds[selectedAdIndex];\n\n        // Update the spent budget for the day\n        selectedAd.SpentToday += CalculateCost(selectedAd);\n\n        // Check if daily budget is reached, and disable ad from today's selections if so\n        if (selectedAd.SpentToday >= selectedAd.DailyBudget)\n            _ads.Remove(selectedAd);\n\n        return selectedAd;\n    }\n\n    // Placeholder method to calculate ad cost on display.\n    private decimal CalculateCost(Ad ad)\n    {\n        // Cost calculation logic can be based on fixed costs or more complex models\n        return 0.25m; // Example: Assume each ad impression costs $0.25\n    }\n\n    public void ResetDailySpending()\n    {\n        foreach (var ad in _ads)\n        {\n            ad.SpentToday = 0;\n        }\n    }\n}\n\n// Usage example\npublic class Program\n{\n    static void Main(string[] args)\n    {\n        var adManager = new AdManager();\n        adManager.AddAd(new Ad { Id = 1, Content = "Buy our gadgets!", MaxBudget = 100m, DailyBudget = 25m });\n        adManager.AddAd(new Ad { Id = 2, Content = "Save on groceries!", MaxBudget = 50m, DailyBudget = 10m });\n\n        Ad selectedAd = adManager.GetAdToShow();\n        Console.WriteLine($"Showing Ad: {selectedAd?.Content}");\n    }\n}