using System;\nusing System.IO;\nusing System.Security.AccessControl;\n\npublic class FolderCreator\n{\n    public void CreateFolderWithPermissions(string folderPath, string userAccount)\n    {\n        // Define the directory security settings\n        DirectorySecurity directorySecurity = new DirectorySecurity();\n\n        // Add a FileSystemAccessRule for the specified user with Modify rights\n        FileSystemAccessRule accessRule = new FileSystemAccessRule(\n            userAccount,\n            FileSystemRights.Modify,\n            InheritanceFlags.ContainerInherit | InheritanceFlags.ObjectInherit,\n            PropagationFlags.None,\n            AccessControlType.Allow);\n\n        directorySecurity.AddAccessRule(accessRule);\n\n        // Create the directory with the specified security settings\n        Directory.CreateDirectory(folderPath, directorySecurity);\n    }\n}\n\n// Usage example:\n// Replace "C:\Your\Folder\Path" with your desired path and\n// "MACHINENAME\\IUSR_MACHINENAME" with the appropriate user account.\nFolderCreator creator = new FolderCreator();\ncreator.CreateFolderWithPermissions("C:\\Your\\Folder\\Path", "MACHINENAME\\IUSR_MACHINENAME");