public void UpdateEntity(Microsoft.Crm.Sdk.CrmService service, Microsoft.CrmSdk.DynamicEntity entity)\n{\n    // Example validation for a required 'name' field\n    if (entity.Properties.Contains("name") && !string.IsNullOrEmpty((string)entity["name"]))\n    {\n        service.Update(entity);\n    }\n    else\n    {\n        throw new Exception("The 'name' field is required.");\n    }\n}