using System;\nusing FAXCOMEXLib;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        try\n        {\n            FaxServer faxServer = new FaxServer();\n            // Configure your fax server and document here\n\n            FaxDocument faxDoc = (FaxDocument)new FaxDocumentClass();\n            faxDoc.Body = @"C:\path\to\faxfile.pdf";\n            faxDoc.Recipients.Add("1234567890", "Recipient Name");\n            faxDoc.Subject = "Fax Test";\n\n            faxServer.Connect("YourFaxServerName");\n            faxDoc.ConnectedSubmit(faxServer);\n        }\n        catch (Exception ex)\n        {\n            System.IO.File.AppendAllText(@"C:\path\to\log.txt", $"{DateTime.Now}: {ex.Message}\n{ex.StackTrace}\n");\n        }\n    }\n}