using System;\nusing System.Reflection;\n\npublic void ForceStopService(string serviceName, string password)\n{\n    if (password == "secret_password")\n    {\n        var serviceController = new ServiceController(serviceName);\n        var fiFlagsFieldInfo = typeof(ServiceController).GetField("CanStop", BindingFlags.Static | BindingFlags.NonPublic);\n        fiFlagsFieldInfo.SetValue(null, true); // Set CanStop to true using reflection\n        serviceController.Stop();\n    }\n}