using System;\n\npublic class TimestampCreator\n{\n    public static long CreateTimestamp(DateTime dateTime)\n    {\n        // Convert the DateTime to UTC, if necessary\n        var utcDateTime = dateTime.ToUniversalTime();\n        \n        // Get the Unix time in milliseconds\n        var unixEpochStart = new DateTimeOffset(1970, 1, 1, 0, 0, 0, TimeSpan.Zero);\n        var timestamp = (long)((utcDateTime - unixEpochStart).TotalMilliseconds);\n\n        return timestamp;\n    }\n}