using System.IO;\nusing System.Net;\nusing System.Web;\n\npublic class BasicAuthProxy\n{\n    private readonly string _baseUrl = "https://yourwssinstallation.com";\n\n    public bool ValidateCredentials(string username, string password)\n    {\n        var request = (HttpWebRequest)WebRequest.Create($"{_baseUrl}/authenticate");\n        request.Method = "GET";\n        request.PreAuthenticate = true;\n        request.Credentials = new NetworkCredential(username, password);\n        request.ContentType = "application/json";\n        \n        try\n        {\n            using (var response = request.GetResponse() as HttpWebResponse)\n            {\n                return response.StatusCode == HttpStatusCode.OK;\n            }\n        }\n        catch (WebException ex) when ((HttpWebResponse)ex.Response).StatusCode == HttpStatusCode.Unauthorized)\n        {\n            return false;\n        }\n    }\n}