using System;\nusing System.Net;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define your webservice URL and proxy settings\n        string webServiceUrl = "http://example.com/service";\n        string proxyAddress = "192.168.1.1:8080"; // Replace with your proxy address and port\n        string proxyUsername = "username"; // Replace with your proxy username\n        string proxyPassword = "password"; // Replace with your proxy password\n\n        // Create a new WebRequest instance.\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create(webServiceUrl);\n\n        // Set the proxy settings for the request\n        WebProxy proxy = new WebProxy(proxyAddress);\n        proxy.Credentials = new NetworkCredential(proxyUsername, proxyPassword);\n        request.Proxy = proxy;\n\n        try\n        {\n            // Send the 'Get' request and wait for response.\n            HttpWebResponse response = (HttpWebResponse)request.GetResponse();\n            \n            // Process server response here \n            Console.WriteLine("Response status code: " + response.StatusCode);\n\n            response.Close();\n        }\n        catch (WebException ex)\n        {\n            if (ex.Response != null)\n            {\n                using (var reader = new StreamReader(ex.Response.GetResponseStream()))\n                {\n                    string result = reader.ReadToEnd();\n                    Console.WriteLine(result);\n                }\n            }\n            else\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}