using System;\nusing System.Linq.Expressions;\nusing System.Web.Mvc;\nusing System.Web.Mvc.Html;\n\npublic static class HtmlHelperExtensions\n{\n    public static MvcForm BeginSecureForm(this HtmlHelper htmlHelper, string actionName, string controllerName)\n    {\n        string cookieName = "XSRF-Token";\n        var token = Guid.NewGuid().ToString();\n        var cookie = new HttpCookie(cookieName) { HttpOnly = true, Value = token };\n        HttpContext.Current.Response.Cookies.Set(cookie);\n\n        htmlHelper.ViewContext.Writer.WriteLine($"<input type='hidden' name='{cookieName}' value='{token}' />");\n\n        return htmlHelper.BeginForm(actionName, controllerName);\n    }\n\n    // Add overloads as needed\n}