using System;\nusing System.Windows.Forms;\n\npublic class NullableDateTimeContainer : Panel\n{\n    private DateTimePicker dateTimePicker;\n    private CheckBox allowNullCheckBox;\n\n    public DateTime? SelectedDate\n    {\n        get\n        {\n            return allowNullCheckBox.Checked ? (DateTime?)null : dateTimePicker.Value;\n        }\n        set\n        {\n            allowNullCheckBox.Checked = !value.HasValue;\n            if (value.HasValue) \n                dateTimePicker.Value = value.Value;\n        }\n    }\n\n    public NullableDateTimeContainer()\n    {\n        dateTimePicker = new DateTimePicker();\n        allowNullCheckBox = new CheckBox();\n        \n        allowNullCheckBox.Text = "No Date";\n        allowNullCheckBox.CheckedChanged += AllowNullCheckBox_CheckedChanged;\n\n        this.Controls.Add(dateTimePicker);\n        this.Controls.Add(allowNullCheckBox);\n\n        // Arrange controls as needed\n        dateTimePicker.Location = new System.Drawing.Point(10, 10);\n        allowNullCheckBox.Location = new System.Drawing.Point(dateTimePicker.Right + 5, dateTimePicker.Top + 2);\n    }\n\n    private void AllowNullCheckBox_CheckedChanged(object sender, EventArgs e)\n    {\n        if (allowNullCheckBox.Checked)\n        {\n            // Disable the date picker when no date is allowed\n            dateTimePicker.Enabled = false;\n        }\n        else\n        {\n            // Enable it back when a date should be selected\n            dateTimePicker.Enabled = true;\n        }\n    }\n}\n\n// Usage in Form\npublic partial class MyForm : Form\n{\n    private NullableDateTimeContainer NullableDateTimeContainer;\n\n    public MyForm()\n    {\n        InitializeComponent();\n\n        NullableDateTimeContainer dtc = new NullableDateTimeContainer();\n        dtc.SelectedDateChanged += Dtc_SelectedDateChanged;\n        this.Controls.Add(dtc);\n        \n        // Position the container as needed\n        dtc.Location = new System.Drawing.Point(10, 10);\n    }\n\n    private void Dtc_SelectedDateChanged(object sender, EventArgs e)\n    {\n        DateTime? selectedDate = ((NullableDateTimeContainer)sender).SelectedDate;\n        if (selectedDate.HasValue)\n        {\n            MessageBox.Show("Selected Date: " + selectedDate.Value.ToShortDateString());\n        }\n        else\n        {\n            MessageBox.Show("No date selected.");\n        }\n    }\n}