using System;\n   using System.Configuration;\n\n   public class ConfigLoader\n   {\n       public static string GetConnectionStringFromConfig(string filePath)\n       {\n           ExeConfigurationFileMap fileMap = new ExeConfigurationFileMap();\n           fileMap.ExeConfigFilename = filePath;\n           \n           Configuration config = ConfigurationManager.OpenMappedExeConfiguration(fileMap, ConfigurationUserLevel.None);\n           \n           return config.AppSettings.Settings["ConnectString"].Value;\n       }\n   }\n\n   // Usage\n   string connectionString = ConfigLoader.GetConnectionStringFromConfig("path/to/your/configfile.config");\n   Console.WriteLine(connectionString);