using System;\n   using System.IO.IsolatedStorage;\n\n   public static void IncreaseIsolatedStorageQuota()\n   {\n       IsolatedStorageFile myStore = IsolatedStorageFile.GetUserStoreForDomain();\n       long currentQuota = myStore.Quota;\n       if (currentQuota < 1024 * 1024 * 50) // Check if the quota is less than 50MB\n       {\n           try\n           {\n               bool quotaIncreased = IsolatedStorageFile.IncreaseQuotaTo(myStore, 1024 * 1024 * 100); // Request 100MB\n               if (quotaIncreased)\n               {\n                   Console.WriteLine("Isolated storage quota increased.");\n               }\n               else\n               {\n                   Console.WriteLine("Failed to increase isolated storage quota.");\n               }\n           }\n           catch (Exception ex)\n           {\n               Console.WriteLine($"Error increasing isolated storage quota: {ex.Message}");\n           }\n       }\n   }