try \n{\n    using (SqlConnection connectionA = new SqlConnection(connectionStringA))\n    {\n        connectionA.Open();\n        using (SqlCommand commandA = new SqlCommand(sqlStatementA, connectionA))\n        {\n            commandA.ExecuteNonQuery();\n        }\n    }\n\n    using (SqlConnection connectionB = new SqlConnection(connectionStringB))\n    {\n        connectionB.Open();\n        using (SqlCommand commandB = new SqlCommand(sqlStatementB, connectionB))\n        {\n            commandB.ExecuteNonQuery();\n        }\n    }\n}\ncatch (Exception ex)\n{\n    Console.WriteLine("An error occurred: " + ex.Message);\n}