using System;\nusing System.Collections.Generic;\nusing System.Web.Mvc;\n\npublic class FileUploadController : Controller\n{\n    // Dictionary to hold the file list by user token.\n    private static readonly IDictionary<string, List<string>> uploadedFiles = new Dictionary<string, List<string>>();\n\n    [HttpGet]\n    public ActionResult Upload()\n    {\n        // Generate a unique token for this user session.\n        var userToken = Guid.NewGuid().ToString();\n        ViewBag.UserToken = userToken;\n        return View();\n    }\n\n    [HttpPost]\n    public JsonResult ReceiveFile(string file, string userToken)\n    {\n        if (!uploadedFiles.ContainsKey(userToken))\n            uploadedFiles[userToken] = new List<string>();\n\n        // Process the file upload here (e.g., save to disk).\n        // Assuming 'file' is a name or path of the received file.\n\n        uploadedFiles[userToken].Add(file); // Add the file to the user's list in memory.\n        \n        return Json(new { Success = true });\n    }\n\n    [HttpGet]\n    public ActionResult ViewUploadedFiles(string userToken)\n    {\n        if (!uploadedFiles.ContainsKey(userToken))\n            ViewBag.Files = new List<string>();\n        else\n            ViewBag.Files = uploadedFiles[userToken];\n\n        return View();\n    }\n}