using System.Data;\nusing System.Data.SqlClient;\n\n// Assuming you have a list of item IDs\nList<int> itemIDs = new List<int> { 1, 2, 3, 4 }; // Example IDs\n\n// Create a StringBuilder to construct the query dynamically\nStringBuilder sql = new StringBuilder("SELECT ItemID, Name, RelDate, Price, Status FROM item_k WHERE ItemID IN (");\nfor (int i = 0; i < itemIDs.Count; i++)\n{\n    sql.Append("@id" + i);\n    if (i < itemIDs.Count - 1)\n        sql.Append(", ");\n}\nsql.Append(")");\n\n// Create a SqlCommand object\nusing (SqlConnection connection = new SqlConnection(connectionString))\n{\n    using (SqlCommand command = new SqlCommand(sql.ToString(), connection))\n    {\n        // Add parameters to the command\n        for (int i = 0; i < itemIDs.Count; i++)\n        {\n            command.Parameters.AddWithValue("@id" + i, itemIDs[i]);\n        }\n\n        // Use a SqlDataAdapter to fill a DataTable with the results of the query\n        using (SqlDataAdapter adapter = new SqlDataAdapter(command))\n        {\n            DataTable dataTable = new DataTable();\n            adapter.Fill(dataTable);\n\n            // Bind the DataTable to your GridView\n            gridView.DataSource = dataTable;\n            gridView.DataBind();\n        }\n    }\n}