using System;\nusing System.IO;\nusing System.Data.SqlClient;\n\npublic void SaveReportToDatabase(string filePath, string reportName)\n{\n    var sql = "INSERT INTO CrystalReports (ReportName, ReportData) VALUES (@reportName, @reportData)";\n    \n    // Read file into a byte array\n    byte[] bytes = File.ReadAllBytes(filePath);\n\n    using (var conn = new SqlConnection("YourConnectionStringHere"))\n    {\n        using (var cmd = new SqlCommand(sql, conn))\n        {\n            cmd.Parameters.AddWithValue("@reportName", reportName);\n            cmd.Parameters.AddWithValue("@reportData", bytes);\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n}