using System;\nusing System.Collections.Generic;\nusing System.Linq;\nusing System.Text.RegularExpressions;\n\npublic class Example\n{\n    public int Id { get; set; }\n    public string Text { get; set; }\n}\n\npublic static void Main()\n{\n    List<Example> examples = new List<Example>\n    {\n        new Example { Id = 1, Text = "abc abccc dabc" },\n        new Example { Id = 2, Text = "abccc dabc" },\n        new Example { Id = 3, Text = "abccc abc dabc" }\n    };\n\n    string pattern = @"\babc\b";\n    var matchedItems = examples.Where(e => Regex.IsMatch(e.Text, pattern)).ToList();\n\n    foreach (var item in matchedItems)\n    {\n        Console.WriteLine($"Id: {item.Id}, Text: {item.Text}");\n    }\n}