using System;\nusing System.Security.Cryptography;\n\npublic class HashVerificationExample\n{\n    public bool VerifyHash(byte[] hashData, byte[] signedHash, RSA publicKey)\n    {\n        try\n        {\n            RSACryptoServiceProvider rsa = new RSACryptoServiceProvider();\n            rsa.ImportParameters(publicKey.ExportParameters(false));\n\n            return rsa.VerifyHash(hashData, CryptoConfig.MapNameToOID("SHA256"), signedHash);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Error verifying hash: {ex.Message}");\n            return false;\n        }\n    }\n\n    public static void Main()\n    {\n        // Example usage of VerifyHash\n        HashVerificationExample example = new HashVerificationExample();\n        bool isValid = example.VerifyHash(/*hashData*/, /*signedHash*/, /*publicKey*/);\n        Console.WriteLine($"Is valid hash: {isValid}");\n    }\n}