using System;\nusing SixLabors.ImageSharp;\nusing SixLabors.ImageSharp.PixelFormats;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string imagePath = "path_to_your_image.jpg";\n\n        using (Image<Rgba32> image = Image.Load<Rgba32>(imagePath))\n        {\n            // Get dimensions\n            int width = image.Width;\n            int height = image.Height;\n\n            Console.WriteLine($"Width: {width} pixels");\n            Console.WriteLine($"Height: {height} pixels");\n\n            // Note: ImageSharp does not expose DPI directly, so if you need that,\n            // you might have to handle it differently, perhaps with metadata parsing.\n        }\n    }\n}