using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string exeName = "example.exe";\n        \n        ProcessStartInfo startInfo = new ProcessStartInfo();\n        startInfo.FileName = exeName;\n        startInfo.WorkingDirectory = @"C:\Path\To\Exe"; // Set the working directory if necessary\n        startInfo.UseShellExecute = false; // Required to redirect streams or set other options\n        startInfo.RedirectStandardOutput = true; // To capture stdout\n        startInfo.RedirectStandardError = true;  // To capture stderr\n\n        try\n        {\n            using (Process process = Process.Start(startInfo))\n            {\n                string output = process.StandardOutput.ReadToEnd();\n                string error = process.StandardError.ReadToEnd();\n\n                Console.WriteLine("Output: " + output);\n                Console.WriteLine("Error: " + error);\n\n                process.WaitForExit();\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}