using System;\nusing System.Linq;\nusing System.Text;\n\npublic class DataScrambler\n{\n    private string _key = "secretKey"; // You should use a better key management strategy\n\n    public string Scramble(string input)\n    {\n        var scrambled = new StringBuilder(input.Length);\n        foreach (char c in input)\n        {\n            if (c == '.')\n            {\n                scrambled.Append('.');\n            }\n            else\n            {\n                // Simple XOR operation with a character of the key\n                char scrambledChar = (char)(c ^ _key[scrambled.Length % _key.Length]);\n                scrambled.Append(scrambledChar);\n            }\n        }\n\n        return scrambled.ToString();\n    }\n\n    public string Descramble(string input)\n    {\n        // Since XOR is reversible with the same operation, we can use the Scramble method to reverse\n        return Scramble(input);\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var scrambler = new DataScrambler();\n        \n        string original = "Jesse.J.James";\n        string scrambled = scrambler.Scramble(original);\n        string descrambled = scrambler.Descramble(scrambled);\n\n        Console.WriteLine($"Original: {original}");\n        Console.WriteLine($"Scrambled: {scrambled}");\n        Console.WriteLine($"Descrambled: {descrambled}");\n    }\n}