using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string sqlPattern = @"^\s*(SELECT|INSERT|UPDATE)\b";\n        Regex regex = new Regex(sqlPattern, RegexOptions.IgnoreCase);\n\n        string[] testQueries = \n        {\n            "SELECT * FROM users",\n            "insert into users (name) values ('John Doe')",\n            "Update employees set salary=50000 where id=1",\n            "delete from orders where id=2" // Should not match\n        };\n\n        foreach (var query in testQueries)\n        {\n            bool isMatch = regex.IsMatch(query);\n            Console.WriteLine($"Query: {query}\nMatches SQL Command: {isMatch}\n");\n        }\n    }\n}