using Azure.Identity;\n  using Azure.Security.KeyVault.Secrets;\n\n  class Program\n  {\n      static async Task Main(string[] args)\n      {\n          // URL of the Azure Key Vault instance\n          string kvUri = "https://my-key-vault.vault.azure.net";\n\n          // Create a new KeyVaultClient instance\n          var client = new SecretClient(new Uri(kvUri), new DefaultAzureCredential());\n\n          // Retrieve the secret (symmetric key in this case)\n          KeyVaultSecret secret = await client.GetSecretAsync("MyEncryptionKey");\n\n          // Access the value of the secret\n          string symmetricKeyValue = secret.Value;\n\n          // Use symmetricKeyValue for encryption/decryption with .NET encryption classes\n      }\n  }