using System.Web.UI.WebControls;\n\n   protected void Page_Load(object sender, EventArgs e)\n   {\n       if (!IsPostBack)\n       {\n           AddRowToTable("Data1", "Data2");\n           AddRowToTable("Data3", "Data4");\n       }\n   }\n\n   private void AddRowToTable(string column1Data, string column2Data)\n   {\n       MyControl row = (MyControl)LoadControl("~/Controls/MyControl.ascx");\n       row.SetData(column1Data, column2Data);\n       MyTable.Rows.Add(row);\n   }