using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string content = "the brown fox jumped over <b>the</b> lazy dog over there";\n        string keyword = "the";\n        string replacement = $"<span style=\"background-color:yellow;\">$0</span>";\n\n        // Regex to match whole words outside HTML tags\n        string pattern = $@"(?<!\S)(?![^<]*>)\b{Regex.Escape(keyword)}\b";\n\n        content = Regex.Replace(content, pattern, replacement, RegexOptions.IgnoreCase);\n\n        Console.WriteLine(content);\n    }\n}