using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        try\n        {\n            // Define the path to appcmd.exe. This might need adjustment based on your system.\n            string appCmdPath = @"%windir%\system32\inetsrv\appcmd";\n\n            // Define the command and arguments for backing up the site configuration.\n            string commandArguments = "backup create \"MyBackup\"";\n\n            // Start a process to execute appcmd.exe with the specified arguments\n            ProcessStartInfo startInfo = new ProcessStartInfo\n            {\n                FileName = appCmdPath,\n                Arguments = commandArguments,\n                UseShellExecute = false,\n                RedirectStandardOutput = true,\n                CreateNoWindow = true\n            };\n\n            // Run the process and capture the output.\n            using (Process process = new Process { StartInfo = startInfo })\n            {\n                process.Start();\n                string output = process.StandardOutput.ReadToEnd();\n                process.WaitForExit();\n\n                // Output the result of the command execution\n                Console.WriteLine(output);\n            }\n\n            Console.WriteLine("Backup completed successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to backup IIS configuration: {ex.Message}");\n        }\n    }\n}