public class ConnectionLogger : IDisposable {\n      private readonly string _connectionString;\n      private readonly DbConnection _dbConnection;\n\n      public ConnectionLogger(string connectionString) {\n          _connectionString = connectionString;\n          _dbConnection = new SqlConnection(connectionString);\n          _dbConnection.Open();\n          Console.WriteLine($"Opened connection: {_connectionString}"); // Replace with logging framework\n      }\n\n      public void Dispose() {\n          if (_dbConnection.State == ConnectionState.Open) {\n              _dbConnection.Close();\n              Console.WriteLine($"Closed connection: {_connectionString}"); // Replace with logging framework\n          }\n      }\n  }