using System;\n   using System.IO;\n   using System.IO.Packaging;\n\n   public static void AddBinaryToOoxml(string filePath, string partUri, byte[] binaryData)\n   {\n       using (Package pkg = Package.Open(filePath, FileMode.OpenOrCreate))\n       {\n           Uri uri = PackUriHelper.CreatePartUri(new Uri(partUri, UriKind.Relative));\n           if (!pkg.PartExists(uri))\n           {\n               PackagePart part = pkg.CreatePart(uri, "application/vnd.openxmlformats-officedocument.oleObject");\n               using (Stream stream = part.GetStream())\n               {\n                   stream.Write(binaryData, 0, binaryData.Length);\n               }\n           }\n       }\n   }