// Configure the ServicePointManager to require certificates\nServicePointManager.SecurityProtocol = SecurityProtocolType.Tls12;\n\nvar binding = new BasicHttpsBinding(BasicHttpsSecurityMode.Transport);\nbinding.Security.Mode = BasicHttpsSecurityMode.Transport;\nbinding.Security.Transport.ClientCredentialType = HttpClientCredentialType.Certificate; // If using client cert\n\nvar endpointAddress = new System.ServiceModel.EndpointAddress("https://yourwebservice.com/YourService.svc");\nvar channelFactory = new ChannelFactory<IYourService>(binding, endpointAddress);\n\n// Set up client credentials securely\nchannelFactory.Credentials.ClientCertificate.SetCertificate(\n    StoreLocation.CurrentUser,\n    StoreName.My,\n    X509FindType.FindBySubjectName,\n    "your-client-certificate-subject"\n);\nchannelFactory.Credentials.ServiceCertificate.Authentication.CertificateValidationMode = X509CertificateValidationMode.PeerOrChainTrust;\n\nvar serviceClient = channelFactory.CreateChannel();