public class User\n{\n    public virtual int Id { get; set; }\n    public virtual string Name { get; set; }\n}\n\n// Mapping in XML or Fluent NHibernate\nclass UserMapping : ClassMap<User>\n{\n    public UserMapping()\n    {\n        Table("Users");\n        Id(x => x.Id);\n        Map(x => x.Name).Length(250);\n    }\n}\n\n// Usage in code\nvar sessionFactory = new Configuration()\n    .Configure().AddAssembly(System.Reflection.Assembly.GetExecutingAssembly())\n    .BuildSessionFactory();\n\nusing (var session = sessionFactory.OpenSession())\n{\n    var user = session.Get<User>(1);  // Loads a user by ID\n}