using System;\nusing Microsoft.WindowsMobile.Telephony;\n\nnamespace FlashSMSApp\n{\n    class Program\n    {\n        static void Main(string[] args)\n        {\n            try\n            {\n                // Create a new SMS message\n                SmsMessage msg = new SmsMessage(SmsMessageRecipientType.PhoneNumber, "1234567890");\n                \n                // Set the text of the message\n                msg.Body = "This is a flash SMS!";\n                \n                // Send as Flash SMS (Class 0)\n                msg.SMSMessageOptions = SMSMessageOptions.Class0;\n\n                // Sending the message\n                SmsDevice device = new SmsDevice();\n                device.SendMessage(msg);\n\n                Console.WriteLine("Flash SMS sent successfully.");\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Failed to send Flash SMS: " + ex.Message);\n            }\n\n            Console.ReadLine();\n        }\n    }\n}