// In both projects, include:\nusing Newtonsoft.Json;\n\n// Define a Data Transfer Object\npublic class DataRowDTO\n{\n    public int Id { get; set; }\n    public string Value1 { get; set; }\n    public string Value2 { get; set; } // Add properties as needed\n}\n\n// App1 code to serialize and call App2\nDataRowDTO row = new DataRowDTO { Id = 1, Value1 = "my var", Value2 = "another value" };\nstring jsonString = JsonConvert.SerializeObject(row);\n\nSystem.Diagnostics.Process.Start("App2.exe", $"\"{jsonString}\"");\n\n// App2 code to deserialize the data\nif (args.Length > 0)\n{\n    string receivedJson = args[0];\n    DataRowDTO row = JsonConvert.DeserializeObject<DataRowDTO>(receivedJson);\n    \n    // Now you can use 'row' object in your application logic\n}