using System;\nusing System.Threading;\n\nclass Program\n{\n    private static AutoResetEvent autoEvent = new AutoResetEvent(false);\n\n    static void Main()\n    {\n        Thread workerThread = new Thread(DoWork);\n        workerThread.Start();\n\n        Console.WriteLine("Main thread does some work, then signals.");\n        autoEvent.Set();\n        workerThread.Join();\n        Console.WriteLine("Main thread finishes.");\n    }\n\n    private static void DoWork()\n    {\n        Console.WriteLine("Worker thread is waiting for the signal.");\n        autoEvent.WaitOne();\n        Console.WriteLine("Worker thread received the signal.");\n    }\n}