using System;\nusing System.Runtime;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        AllocationTracking.Enable();\n        \n        // Your code that allocates objects\n        \n        AllocationTracking.Disable();\n        var allocations = AllocationTracking.GetInfo();\n\n        foreach (var allocation in allocations)\n        {\n            Console.WriteLine($"Type: {allocation.Type.Name}, Size: {allocation.SizeInBytes} bytes");\n        }\n    }\n}