using System;\nusing System.Net.Mail;\n\npublic class FaxSender\n{\n    public void SendFax(string recipientEmail, string pdfPath)\n    {\n        // Create an instance of the MailMessage class.\n        MailMessage mail = new MailMessage();\n\n        try\n        {\n            // Set the sender's address and display name.\n            mail.From = new MailAddress("your-email@example.com", "Your Name");\n\n            // Add a recipient.\n            mail.To.Add(new MailAddress(recipientEmail));\n\n            // Specify the message content.\n            mail.Body = "Please find attached the document you requested.";\n\n            if (!string.IsNullOrEmpty(pdfPath))\n            {\n                System.Net.Mail.Attachment attachment;\n                attachment = new System.Net.Mail.Attachment(pdfPath);\n                mail.Attachments.Add(attachment);\n            }\n\n            // Set the SMTP server to use and send the message.\n            SmtpClient smtpClient = new SmtpClient("smtp.yourserver.com", 587)\n            {\n                Credentials = new System.Net.NetworkCredential("your-email@example.com", "yourpassword"),\n                EnableSsl = true\n            };\n\n            smtpClient.Send(mail);\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error sending fax: {0}", ex.Message);\n        }\n        finally\n        {\n            mail.Dispose();\n        }\n    }\n}