protected void foo(object sender, EventArgs e)\n{\n    string pdfPath = GeneratePDF(); // Assuming your method returns the path to the generated PDF.\n    \n    Response.Clear();\n    Response.AddHeader("Content-Disposition", "inline; filename=\"" + Path.GetFileName(pdfPath) + "\"");\n    Response.ContentType = "application/pdf";\n    \n    using (FileStream fileStream = new FileStream(pdfPath, FileMode.Open))\n    {\n        byte[] bytes = new byte[fileStream.Length];\n        fileStream.Read(bytes, 0, (int)fileStream.Length);\n        Response.BinaryWrite(bytes);            \n    }\n    \n    Response.End();\n\n    // Optionally you could redirect to a page that has the iframe or JavaScript to print\n}