// Example: Avoiding DatabaseHelper - use Dependency Injection instead for database access\n   public interface IDataRepository {\n       void Save(Data data);\n   }\n\n   public class SqlDataRepository : IDataRepository { /*...*/ }\n   \n   public class UserRepository {\n       private readonly IDataRepository _repository;\n\n       public UserRepository(IDataRepository repository) => _repository = repository;\n       \n       public void AddUser(User user) {\n           Validate(user);\n           _repository.Save(user); // Strategy pattern in action\n       }\n\n       private void Validate(User user) { /*...*/ }\n   }