using System;\nusing System.Diagnostics;\nusing System.Runtime.InteropServices;\nusing System.Windows.Forms;\n\nnamespace KeyDetectionApp\n{\n    public partial class MainForm : Form\n    {\n        [DllImport("user32.dll")]\n        private static extern short GetKeyState(int nVirtKey);\n\n        private const int VK_SHIFT = 0x10;\n\n        public MainForm()\n        {\n            InitializeComponent();\n        }\n\n        private void MainForm_Load(object sender, EventArgs e)\n        {\n            // Check if the Shift key is pressed\n            bool shiftKeyPressed = ((GetKeyState(VK_SHIFT) & 0x8000) != 0);\n\n            if (shiftKeyPressed)\n            {\n                // Shift key was held down on startup\n                MessageBox.Show("Shift key was detected on application startup.");\n                // Perform alternative workflow here.\n            }\n            else\n            {\n                // Standard workflow.\n                MessageBox.Show("Standard startup.");\n            }\n        }\n\n        [STAThread]\n        static void Main()\n        {\n            Application.EnableVisualStyles();\n            Application.SetCompatibleTextRenderingDefault(false);\n\n            using (MainForm form = new MainForm())\n            {\n                // Additional setup before the form becomes visible\n                form.Load += form.MainForm_Load;\n                Application.Run(form);\n            }\n        }\n    }\n}