using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        string executablePath = @"\\machine_name\share_name\directory\runme.exe";\n\n        try\n        {\n            ProcessStartInfo startInfo = new ProcessStartInfo(executablePath);\n            // Specify any additional arguments if required\n            // startInfo.Arguments = "your-arguments-here";\n            \n            // Set whether to run in a new window or not\n            startInfo.CreateNoWindow = true;\n            startInfo.UseShellExecute = false;\n\n            using (Process process = Process.Start(startInfo))\n            {\n                process.WaitForExit();\n                Console.WriteLine("Executable exited with code: " + process.ExitCode);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error starting the process: " + ex.Message);\n        }\n    }\n}