using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string fileExtension = ".html"; // Example extension\n        string mimeType = GetMimeTypeForFileExtension(fileExtension);\n        Console.WriteLine("MIME Type for '{0}': {1}", fileExtension, mimeType ?? "Not found");\n    }\n\n    public static string GetMimeTypeForFileExtension(string fileExtension)\n    {\n        using (var searcher = new ManagementObjectSearcher(\n            "SELECT MimeType FROM IIsMimeMap WHERE Extension='" + fileExtension.ToLower() + "'",\n            new ObjectQuery(),\n            new EnumerationOptions { UseAmendedQualifiers = false }))\n        {\n            foreach (ManagementBaseObject managementBase in searcher.Get())\n            {\n                return managementBase["MimeType"] as string;\n            }\n        }\n\n        return null;\n    }\n}