using System;\nusing ManagedWiFi;\n\nclass Program\n{\n    static void Main()\n    {\n        WlanClient client = new WlanClient();\n\n        foreach (WlanClient.WlanInterface wlanIface in client.Interfaces)\n        {\n            WlanAssociationAttributes associationAttributes = wlanIface.GetCurrentConnection().wlanAssociationAttributes;\n            string ssid = associationAttributes.dot11Ssid.SSID;\n\n            Console.WriteLine("Connected SSID: " + ssid);\n        }\n    }\n}