using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string processName = "notepad"; // Change this to your target process name\n\n        try\n        {\n            // Create an object searcher with the query for selecting processes\n            ObjectQuery query = new ObjectQuery($"SELECT CommandLine FROM Win32_Process WHERE Name = '{processName}.exe'");\n            \n            using (ManagementObjectSearcher searcher = new ManagementObjectSearcher(query))\n            {\n                foreach (ManagementBaseObject process in searcher.Get())\n                {\n                    Console.WriteLine("Command Line: " + (string)process["CommandLine"]);\n                }\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("An error occurred: " + ex.Message);\n        }\n    }\n}