protected void Page_Load(object sender, EventArgs e)\n   {\n       if (IsPostBack)\n       {\n           string hiddenValues = hiddenDropDownValues.Value;\n           if (!string.IsNullOrEmpty(hiddenValues))\n           {\n               string[] parts = hiddenValues.Split(';');\n               foreach (var part in parts)\n               {\n                   string[] keyValue = part.Split(':');\n                   if (keyValue.Length == 2)\n                   {\n                       // Here, you can add the dynamically added options back to the DropDownList if needed\n                       // Or handle them as per your logic.\n                       string value = keyValue[0];\n                       string text = keyValue[1];\n\n                       // Example: Adding to dropdownlist again (if needed)\n                       MyDropDownList.Items.Add(new System.Web.UI.WebControls.ListItem(text, value));\n                   }\n               }\n           }\n       }\n   }