using System;\nusing Microsoft.ServiceModel.Web;\n\nclass Program\n{\n    static void Main()\n    {\n        using (var client = new WebServiceClient<MyRestfulService>())\n        {\n            try\n            {\n                var result = client.GetResource();\n                Console.WriteLine(result);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine("Error: " + ex.Message);\n            }\n        }\n    }\n}\n\n[ServiceContract]\npublic interface MyRestfulService\n{\n    [OperationContract, WebGet(UriTemplate = "/resource")]\n    string GetResource();\n}