using System;\nusing System.Security.Cryptography;\nusing System.Text;\n\npublic class RSAExample\n{\n    public static void Main()\n    {\n        // Create instance of RSACryptoServiceProvider.\n        using (RSACryptoServiceProvider rsa = new RSACryptoServiceProvider())\n        {\n            string originalData = "Here is some data to sign";\n            byte[] originalBytes = Encoding.UTF8.GetBytes(originalData);\n\n            // Sign the data with the private key.\n            byte[] signedBytes = rsa.SignData(originalBytes, CryptoConfig.MapNameToOID("SHA256"));\n\n            Console.WriteLine("Signed Data:");\n            Console.WriteLine(Convert.ToBase64String(signedBytes));\n\n            // Verify the data using the public key.\n            bool isVerified = rsa.VerifyData(originalBytes, CryptoConfig.MapNameToOID("SHA256"), signedBytes);\n\n            if (isVerified)\n                Console.WriteLine("The signature was verified.");\n            else\n                Console.WriteLine("The signature could not be verified.");\n        }\n    }\n}