using System;\nusing Ghostscript.NET.Rasterizer;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string sourcePath = @"path\to\your\source.pdf";\n        string outputPath = @"path\to\your\output.png";\n\n        using (var rasterizer = new Rasterizer())\n        {\n            var pdfSettings = new GhostscriptRasterizationOptions\n            {\n                Format = GhostscriptSharp.Rasterizer.GhostscriptSharp.Format.png16m,\n                Height = 792, // 11 inches at 72 DPI\n                Width = 612   // 8.5 inches at 72 DPI\n            };\n\n            rasterizer.Open(sourcePath);\n            var pages = rasterizer.GetPageCount();\n            \n            for (int i = 0; i < pages; i++)\n            {\n                string currentOutputPath = $"{outputPath}-page{i + 1}.png";\n                using (var bitmap = rasterizer.GetPageAsBitmap(i, pdfSettings))\n                {\n                    bitmap.Save(currentOutputPath);\n                }\n            }\n\n            Console.WriteLine($"Converted {pages} page(s) from PDF to PNG.");\n        }\n    }\n}