// Sample Dependency Injection Configuration with Unity in WPF (Prism)\nprotected override void ConfigureContainer(IContainerRegistry containerRegistry)\n{\n    containerRegistry.RegisterForNavigation<MainView, MainViewModel>();\n    containerRegistry.AddTransient<IMyService, MyService>();\n\n    // Register Dependencies\n    containerRegistry.RegisterInstance<IMyRepository>(new MyRepository(new DbContext()));\n}