public class DatabaseAccess\n{\n    private readonly IDatabaseAdapter _databaseAdapter;\n\n    public DatabaseAccess(IDatabaseAdapter databaseAdapter)\n    {\n        _databaseAdapter = databaseAdapter;\n    }\n\n    public void ExecuteCommand(string sql, params (string name, object value)[] parameters)\n    {\n        using var connection = _databaseAdapter.GetConnection();\n        using IDbCommand command = _databaseAdapter.CreateCommand(sql, CommandType.Text);\n        \n        foreach (var (name, value) in parameters)\n        {\n            var parameter = command.CreateParameter();\n            parameter.ParameterName = name.Replace("@", string.Empty);\n            parameter.Value = value;\n            command.Parameters.Add(parameter);\n        }\n\n        connection.Open();\n        command.ExecuteNonQuery();\n    }\n}